//	Canonicalise a url
//	http://au.php.net/manual/en/function.realpath.php#71334
function canonicaliseUrl($url)
{
	$url = explode('/', $url);
	$keys = array_keys($url, '..');

	foreach($keys AS $keypos => $key)
		array_splice($url, $key - ($keypos * 2 + 1), 2);

	$url = implode('/', $url);
	return str_replace('./', '', $url);
}

