operation.search:
// Clean the request variables, add slashes, etc.
cleanRequest();
$context = array();

operation.add:
$context = array('pretty' => array());
// Unserialize the array of pretty board URLs
$context['pretty']['board_urls'] = unserialize($modSettings['pretty_board_urls']);
// Clean the request variables, add slashes, etc.
cleanRequest();

// Now make absolutely sure it's a number.
// Check for pretty board URLs too!
$board = (int) (is_numeric($_REQUEST['board']) ? $_REQUEST['board'] : array_search(mb_strtolower($_REQUEST['board'], 'UTF-8'), $context['pretty']['board_urls']));
